/*Q.Write a Java program to create a new array list, add some colors (string) and print out 
the collection. */
package assignmentR;

import java.util.ArrayList;

public class Arraylistp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      ArrayList<String> colour =new ArrayList<>(); 
      colour.add("Red");
      colour.add("Blue");
      colour.add("Black");
      colour.add("Green");
      colour.add("Yellow");
      colour.add("White");
      colour.add("Orange");
      
      System.out.println("Colours:-"+colour);
	}

}
