package assignmentR;

public class Patter2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1;
		int i=1;
		while(a<=10) {
			int j=1;
			while(j<=i) {
				System.out.print(a);
				j++;
				a++;
			}
			System.out.println();
			i++;
		}
		
	}

}
